INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-07-18 14:01:06', '2017-07-18 17:01:06', 'Bem-vindo ao WordPress. Esse é o seu primeiro post. Edite-o ou exclua-o, e então comece a escrever!', 'Olá, mundo!', '', 'publish', 'open', 'open', '', 'ola-mundo', '', '', '2017-07-18 14:01:06', '2017-07-18 17:01:06', '', 0, 'http://localhost/whitehouse/?p=1', 0, 'post', '', 1),
(2, 1, '2017-07-18 14:01:06', '2017-07-18 17:01:06', 'Esta é uma página de exemplo. É diferente de um post porque ela ficará em um local e será exibida na navegação do seu site (na maioria dos temas). A maioria das pessoas começa com uma página de introdução aos potenciais visitantes do site. Ela pode ser assim:\n\n<blockquote>Olá! Eu sou um bike courrier de dia, ator amador à noite e este é meu blog. Eu moro em São Paulo, tenho um cachorro chamado Tonico e eu gosto de caipirinhas. (E de ser pego pela chuva.)</blockquote>\n\nou assim:\n\n<blockquote>A XYZ foi fundada em 1971 e desde então vem proporcionando produtos de qualidade a seus clientes. Localizada em Valinhos, XYZ emprega mais de 2.000 pessoas e faz várias contribuições para a comunidade local.</blockquote>\nComo um novo usuário do WordPress, você deve ir até o <a href="http://localhost/whitehouse/wp-admin/">seu painel</a> para excluir essa página e criar novas páginas com seu próprio conteúdo. Divirta-se!', 'Página de Exemplo', '', 'publish', 'closed', 'open', '', 'pagina-exemplo', '', '', '2017-07-18 14:01:06', '2017-07-18 17:01:06', '', 0, 'http://localhost/whitehouse/?page_id=2', 0, 'page', '', 0),
(3, 1, '2017-07-18 14:01:43', '0000-00-00 00:00:00', '', 'Rascunho automático', '', 'auto-draft', 'open', 'open', '', '', '', '', '2017-07-18 14:01:43', '0000-00-00 00:00:00', '', 0, 'http://localhost/whitehouse/?p=3', 0, 'post', '', 0),
(4, 1, '2017-07-18 14:10:42', '0000-00-00 00:00:00', '', 'Espresso', '', 'auto-draft', 'open', 'closed', '', '', '', '', '2017-07-18 14:10:42', '0000-00-00 00:00:00', '', 0, 'http://localhost/whitehouse/wp-content/uploads/2017/07/espresso.jpg', 0, 'attachment', 'image/jpeg', 0),
(5, 1, '2017-07-18 14:10:43', '0000-00-00 00:00:00', '', 'Sanduíche', '', 'auto-draft', 'open', 'closed', '', '', '', '', '2017-07-18 14:10:43', '0000-00-00 00:00:00', '', 0, 'http://localhost/whitehouse/wp-content/uploads/2017/07/sandwich.jpg', 0, 'attachment', 'image/jpeg', 0),
(6, 1, '2017-07-18 14:10:44', '0000-00-00 00:00:00', '', 'Café', '', 'auto-draft', 'open', 'closed', '', '', '', '', '2017-07-18 14:10:44', '0000-00-00 00:00:00', '', 0, 'http://localhost/whitehouse/wp-content/uploads/2017/07/coffee.jpg', 0, 'attachment', 'image/jpeg', 0),
(7, 1, '2017-07-18 14:10:45', '0000-00-00 00:00:00', 'Bem-vindo(a) ao seu site! Está é sua página inicial, que é a página que a maioria dos visitantes vai ver quando acessarem o seu site pela primeira vez.', 'Página inicial', '', 'auto-draft', 'closed', 'closed', '', '', '', '', '2017-07-18 14:10:45', '0000-00-00 00:00:00', '', 0, 'http://localhost/whitehouse/?page_id=7', 0, 'page', '', 0),
(8, 1, '2017-07-18 14:10:45', '0000-00-00 00:00:00', 'Você pode ser um artista que queira se apresentar aqui, ou talvez queira descrever a missão da sua empresa.', 'Sobre', '', 'auto-draft', 'closed', 'closed', '', '', '', '', '2017-07-18 14:10:45', '0000-00-00 00:00:00', '', 0, 'http://localhost/whitehouse/?page_id=8', 0, 'page', '', 0),
(9, 1, '2017-07-18 14:10:45', '0000-00-00 00:00:00', 'Esta é uma página com algumas informações básicas de contato, como um endereço e número de telefone. Você também pode experimentar usar um plugin para adicionar um formulário de contato.', 'Contato', '', 'auto-draft', 'closed', 'closed', '', '', '', '', '2017-07-18 14:10:45', '0000-00-00 00:00:00', '', 0, 'http://localhost/whitehouse/?page_id=9', 0, 'page', '', 0),
