(152, 1, '2017-07-18 22:34:00', '2017-07-19 01:34:00', '{\n    "hestia::hestia_features_content": {\n        "value": "[{\\"icon_value\\":\\"fa-calendar\\",\\"color\\":\\"#e91e63\\",\\"text\\":\\"Agenda uma partida de Futebol Sui\\u00e7o ou Futebol de Sal\\u00e3o com a nossa equipe\\",\\"link\\":\\"http://localhost/whitehouse/contato/\\",\\"title\\":\\"Agenda\\",\\"subtitle\\":\\"undefined\\",\\"social_repeater\\":\\"undefined\\",\\"id\\":\\"customizer_repeater_56d7ea7f40b56\\",\\"shortcode\\":\\"undefined\\"},{\\"icon_value\\":\\"fa-check\\",\\"color\\":\\"#00bcd4\\",\\"text\\":\\"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.\\",\\"link\\":\\"#\\",\\"title\\":\\"Quality\\",\\"subtitle\\":\\"undefined\\",\\"social_repeater\\":\\"undefined\\",\\"id\\":\\"customizer_repeater_56d7ea7f40b66\\",\\"shortcode\\":\\"undefined\\"},{\\"icon_value\\":\\"fa-support\\",\\"color\\":\\"#4caf50\\",\\"text\\":\\"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.\\",\\"link\\":\\"#\\",\\"title\\":\\"Support\\",\\"subtitle\\":\\"undefined\\",\\"social_repeater\\":\\"undefined\\",\\"id\\":\\"customizer_repeater_56d7ea7f40b86\\",\\"shortcode\\":\\"undefined\\"}]",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "hestia::hestia_testimonials_content": {\n        "value": "[{\\"text\\":\\"&quot;We have no regrets! After using your product my business skyrocketed! I made back the purchase price in just 48 hours! I couldn&#39;t have asked for more than this.&quot;\\",\\"link\\":\\"\\",\\"image_url\\":\\"http://localhost/whitehouse/wp-content/themes/hestia/assets/img/5.jpg\\",\\"title\\":\\"Inverness McKenzie\\",\\"subtitle\\":\\"Business Owner\\",\\"social_repeater\\":\\"undefined\\",\\"id\\":\\"customizer_repeater_56d7ea7f40d56\\",\\"shortcode\\":\\"undefined\\"},{\\"text\\":\\"&quot;Your company is truly upstanding and is behind its product 100 percent. Hestia is worth much more than I paid. I like Hestia more each day because it makes easier.&quot;\\",\\"link\\":\\"\\",\\"image_url\\":\\"http://localhost/whitehouse/wp-content/themes/hestia/assets/img/6.jpg\\",\\"title\\":\\"Hanson Deck\\",\\"subtitle\\":\\"Independent Artist\\",\\"social_repeater\\":\\"undefined\\",\\"id\\":\\"customizer_repeater_56d7ea7f40d66\\",\\"shortcode\\":\\"undefined\\"},{\\"text\\":\\"&quot;Thank you for making it painless, pleasant and most of all hassle free! I am so pleased with this product. Dude, your stuff is great! I will refer everyone I know.&quot;\\",\\"link\\":\\"\\",\\"image_url\\":\\"http://localhost/whitehouse/wp-content/themes/hestia/assets/img/7.jpg\\",\\"title\\":\\"Natalya Undergrowth\\",\\"subtitle\\":\\"Freelancer\\",\\"social_repeater\\":\\"undefined\\",\\"id\\":\\"customizer_repeater_56d7ea7f40d76\\",\\"shortcode\\":\\"undefined\\"}]",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "hestia::hestia_team_content": {\n        "value": "[{\\"icon_value\\":\\"fa-facebook\\",\\"text\\":\\"Locavore pinterest chambray affogato art party, forage coloring book typewriter. Bitters cold selfies, retro celiac sartorial mustache.\\",\\"link\\":\\"\\",\\"image_url\\":\\"http://localhost/whitehouse/wp-content/themes/hestia/assets/img/1.jpg\\",\\"title\\":\\"Desmond Purpleson\\",\\"subtitle\\":\\"CEO\\",\\"social_repeater\\":\\"[{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb908674e06&quot;,&quot;link&quot;:&quot;facebook.com&quot;,&quot;icon&quot;:&quot;fa-facebook&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb9148530ft&quot;,&quot;link&quot;:&quot;plus.google.com&quot;,&quot;icon&quot;:&quot;fa-google-plus&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb9148530fc&quot;,&quot;link&quot;:&quot;twitter.com&quot;,&quot;icon&quot;:&quot;fa-twitter&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb9150e1e89&quot;,